// A tsconfig.json file will be autogenerated to point "lapol/mod" to the correct directory.
import { CommandArguments as Args, Expr, ModuleLoader } from "lapol/mod";

export function load(loader: ModuleLoader): void {
    loader.exportCommands(commands);
}

const commands = {
    h1: (f: Args) => new Expr("h1", f.ca(0)),
    h2: (f: Args) => new Expr("h2", f.ca(0)),
    h3: (f: Args) => new Expr("h3", f.ca(0)),
    h4: (f: Args) => new Expr("h4", f.ca(0)),
    h5: (f: Args) => new Expr("h5", f.ca(0)),
    h6: (f: Args) => new Expr("h6", f.ca(0)),
    it: (f: Args) => new Expr("i", f.ca(0)),
};
